    private void btnGuardarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnGuardarActionPerformed
    
        //Compruebo que no haya campos vacios
        if(txtDNI.getText().isEmpty() || txtModulo.getText().isEmpty() || txtNota.getText().isEmpty() || txtRecuperacion.getText().isEmpty()){
           //Muestro mensaje de error avisando de que hay campos vacios
            JOptionPane.showMessageDialog(Tarea1DI.interfaz, "Parece que has introducido campos vacios", "Error", JOptionPane.ERROR_MESSAGE);     
        }
        else{
            try{
                //Guardo ambos datos en variables para intentar pasarlos a double y nos sirva de chivato, por si intentan meter texto.
                double nota = Double.parseDouble(txtNota.getText());
                double recuperacion =  Double.parseDouble(txtRecuperacion.getText());
                
                //Si el valor introducido no cuadra con lo especificado, dar谩 el siguiente error.
                if((nota < 0 || nota > 10) && (recuperacion < 0 || recuperacion > 5)){
                    JOptionPane.showMessageDialog(Tarea1DI.interfaz, "Parece que el valor del campo nota y recuperaci贸n son incorrectos\nRecuerda:\nEl campo nota es de 0 - 10\nEl campo recuperaci贸n es de 0 - 5.", "Error Num茅rico", JOptionPane.ERROR_MESSAGE);

                }
                //En el caso contrario entrar铆a en el else
                else{
                    //Ahora para acotar el mensaje he creado tres opciones (1. Si la nota est谩 fuera de rango, 2. si la nota de recuperaci贸n est谩 fuera de rango 3. todo ok guardamos datos en la tabla.)
                    if(nota < 0 || nota > 10){
                        JOptionPane.showMessageDialog(Tarea1DI.interfaz, "Parece que el valor de la nota es incorrecto\nRecuerda que solo se admiten valores de 0 - 10.", "Error Num茅rico", JOptionPane.ERROR_MESSAGE);

                        
                    }
                    else if(recuperacion < 0 || recuperacion > 5){
                        JOptionPane.showMessageDialog(Tarea1DI.interfaz, "Parece que el valor de la nota de recuperaci贸n es incorrecto\nRecuerda que solo se admiten valores de 0 - 5.", "Error Num茅rico", JOptionPane.ERROR_MESSAGE);

                    }
                    else{
                        //En este caso est谩 todo OK, por lo que podemos acceder a a帽adir los datos a la tabla
                        DefaultTableModel modelo = (DefaultTableModel) regCalificaciones.getModel();
                        Object registro [] = {txtDNI.getText(), txtModulo.getText(), txtNota.getText(), txtRecuperacion.getText()};
                        
                        //A帽adimos los datos a nuestra tabla
                        modelo.addRow(registro);
                        
                        //Vaciamos los JTextField para permitir volver a escribir el siguiente registro con m谩s facilidad.
                        txtDNI.setText("");
                        txtModulo.setText("");
                        txtNota.setText("");
                        txtRecuperacion.setText("");
  
                        
                    }
                }
                
            }
            // En el caso de utilizar valores no num茅ricos en el campo nota y recuperaci贸n nos devolver铆a este mensaje.
            catch(Exception ex){
                JOptionPane.showMessageDialog(Tarea1DI.interfaz, "Recuerda que solo puedes utilizar valores n煤mericos\nEl campo nota es de 0 - 10\nEl campo recuperaci贸n es de 0 - 5.", "Error Num茅rico", JOptionPane.ERROR_MESSAGE);
            }
            
        }
        
    }//GEN-LAST:event_btnGuardarActionPerformed

   

